css:
/*font!!!!*/
@import url('https://fonts.googleapis.com/css2?family=Dancing+Script&display=swap');
/* adds logo to the searchbar */
#searchbar {
  /* use any direct link to an image you want */
  background: url('https://sreeharip-s.github.io/bonjourr/icons/heart.png') no-repeat 10px center;
  /* you might need to play around with the two following values to get to a fitting result */
	/* ive found that these two values fit so- keep them don't TOUCH them unless u change the image... which idk why you will so- don't ~ Sree*/
  background-size: 20px;
  padding-left: 40px;
}

#searchbar::placeholder {
  color: white;
  opacity: 0.7;
}

#time {
	font-family: 'Dancing Script', cursive; /* or make this any other font in Google Font, make sure to update the url on top*/
	color: #b1243d;
}

/* Reset the inner date part to a system/default font */
#time .clock-date,
#time .clock-date span {
	font-family: system-ui, sans-serif !important;
	font-weight: normal;
}

/* Hides photo author credits. */

#credit {
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.4s, visibility 0.4s;
}

#credit-container:hover #credit {
  visibility: visible;
  opacity: 1;
}



#searchbar {
	box-shadow: 0 0 0px 0px rgba(177, 36, 61, 0.3
		);
	transition: box-shadow 0.4s ease-in-out, background-color 0.4s ease-in-out;
	border: none;
	outline: none;
	background-color: rgba(0, 0, 0, 0.312);
}

#searchbar:focus {
	background-color: rgba(177, 36, 61, 0.3);
	box-shadow: 0 0 10px 2px rgba(177, 36, 61, 0.5); 
}

.digital span {
	transition: text-shadow 0.3s ease-in-out;
}

#searchbar:hover,
#searchbar:focus {
	background-color: rgba(177, 36, 61, 0.2); /* slight red tint */
	box-shadow: 0 0 10px 2px rgba(177, 36, 61, 0.5); /* glow */
	top: 3px; /* moves the entire bar down */
}

.digital span:hover {
	text-shadow: 0 0 8px rgba(177, 36, 61, 1), 0 0 12px rgba(177, 36, 61, 0.8);

	cursor: default; /* optional: avoids the text selection cursor */
}

.digital:hover {
	text-shadow: 0 0 6px rgba(177, 36, 61, 0.8); /* red glow */
}

#weather-icon {
  filter: sepia(999999) hue-rotate(300deg) saturate(999%) brightness();
  transition: filter 0.3s ease-in-out;
}
